import React, { useState, useEffect, useRef } from 'react';
import { Container, Paper, Tabs, Tab, Box, Typography, Button, TextField, Grid, Card, CardContent, CardMedia, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, Alert, Chip, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Select, MenuItem, FormControl, InputLabel } from '@mui/material';
import { Edit, Delete, Add, CloudUpload, Save, Cancel } from '@mui/icons-material';
import axios from 'axios';

const AdminDashboard = () => {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [activeTab, setActiveTab] = useState(0);
  const [newProduct, setNewProduct] = useState({ name: '', description: '', price: '', stock: '', image_url: '' });
  const [editingProduct, setEditingProduct] = useState(null);
  const [editForm, setEditForm] = useState({ name: '', description: '', price: '', stock: '', image_url: '' });
  const [imageFile, setImageFile] = useState(null);
  const [editImageFile, setEditImageFile] = useState(null);
  const [imagePreview, setImagePreview] = useState('');
  const [editImagePreview, setEditImagePreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const fileInputRef = useRef(null);
  const editFileInputRef = useRef(null);

  useEffect(() => {
    if (activeTab === 0) fetchProducts();
    else if (activeTab === 1) fetchOrders();
    else if (activeTab === 2) fetchUsers();
  }, [activeTab]);

  const fetchProducts = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://localhost:8000/api/products.php', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
      setMessage('Failed to load products');
    }
  };

  const fetchOrders = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://localhost:8000/api/orders.php?admin=true', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
      setMessage('Failed to load orders');
    }
  };

  const fetchUsers = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://localhost:8000/api/users.php', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
      setMessage('Failed to load users');
    }
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImageFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setImagePreview(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const handleEditImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setEditImageFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setEditImagePreview(e.target.result);
      reader.readAsDataURL(file);
    }
  };

  const addProduct = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem('token');

    try {
      let imageUrl = newProduct.image_url;

      // If there's an uploaded file, we'd need to upload it to a server first
      // For now, we'll use the image_url field
      if (imageFile) {
        // In a real application, you'd upload the file to a server and get back a URL
        // For demo purposes, we'll use a placeholder
        imageUrl = imagePreview;
      }

      const productData = { ...newProduct, image_url: imageUrl };

      await axios.post('http://localhost:8000/api/products.php', productData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setNewProduct({ name: '', description: '', price: '', stock: '', image_url: '' });
      setImageFile(null);
      setImagePreview('');
      setMessage('Product added successfully!');
      fetchProducts();
    } catch (error) {
      console.error('Error adding product:', error);
      setMessage('Failed to add product');
    } finally {
      setLoading(false);
    }
  };

  const startEditProduct = (product) => {
    setEditingProduct(product);
    setEditForm({
      name: product.name,
      description: product.description,
      price: product.price,
      stock: product.stock,
      image_url: product.image_url
    });
    setEditImagePreview(product.image_url);
  };

  const cancelEdit = () => {
    setEditingProduct(null);
    setEditForm({ name: '', description: '', price: '', stock: '', image_url: '' });
    setEditImageFile(null);
    setEditImagePreview('');
  };

  const updateProduct = async (e) => {
    e.preventDefault();
    setLoading(true);
    const token = localStorage.getItem('token');

    try {
      let imageUrl = editForm.image_url;

      if (editImageFile) {
        // In a real application, you'd upload the file to a server
        imageUrl = editImagePreview;
      }

      const productData = { ...editForm, image_url: imageUrl };

      await axios.put(`http://localhost:8000/api/products.php?id=${editingProduct.id}`, productData, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMessage('Product updated successfully!');
      cancelEdit();
      fetchProducts();
    } catch (error) {
      console.error('Error updating product:', error);
      setMessage('Failed to update product');
    } finally {
      setLoading(false);
    }
  };

  const deleteProduct = async (productId) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;

    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:8000/api/products.php?id=${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Product deleted successfully!');
      fetchProducts();
    } catch (error) {
      console.error('Error deleting product:', error);
      setMessage('Failed to delete product');
    }
  };

  const updateOrderStatus = async (orderId, status) => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(`http://localhost:8000/api/orders.php?id=${orderId}`, { status }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Order status updated successfully!');
      fetchOrders();
    } catch (error) {
      console.error('Error updating order:', error);
      setMessage('Failed to update order status');
    }
  };

  return (
    <div style={{ maxWidth: '1200px', margin: '20px auto', padding: '20px' }}>
      <h1>Admin Dashboard</h1>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={() => setActiveTab('products')} style={{ margin: '0 10px', padding: '10px' }}>Products</button>
        <button onClick={() => setActiveTab('orders')} style={{ margin: '0 10px', padding: '10px' }}>Orders</button>
        <button onClick={() => setActiveTab('users')} style={{ margin: '0 10px', padding: '10px' }}>Users</button>
      </div>

      {activeTab === 'products' && (
        <div>
          <h2>Products</h2>
          <form onSubmit={addProduct} style={{ marginBottom: '20px', padding: '15px', border: '1px solid #dee2e6', borderRadius: '8px' }}>
            <h3>Add New Product</h3>
            <input type="text" placeholder="Name" value={newProduct.name} onChange={(e) => setNewProduct({...newProduct, name: e.target.value})} required style={{ margin: '5px', padding: '8px', width: '200px' }} />
            <input type="text" placeholder="Description" value={newProduct.description} onChange={(e) => setNewProduct({...newProduct, description: e.target.value})} required style={{ margin: '5px', padding: '8px', width: '200px' }} />
            <input type="number" placeholder="Price" value={newProduct.price} onChange={(e) => setNewProduct({...newProduct, price: e.target.value})} required style={{ margin: '5px', padding: '8px', width: '100px' }} />
            <input type="number" placeholder="Stock" value={newProduct.stock} onChange={(e) => setNewProduct({...newProduct, stock: e.target.value})} required style={{ margin: '5px', padding: '8px', width: '100px' }} />
            <input type="text" placeholder="Image URL" value={newProduct.image_url} onChange={(e) => setNewProduct({...newProduct, image_url: e.target.value})} required style={{ margin: '5px', padding: '8px', width: '200px' }} />
            <button type="submit" style={{ margin: '5px', padding: '8px 16px' }}>Add Product</button>
          </form>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '15px' }}>
            {products.map(product => (
              <div key={product.id} style={{ border: '1px solid #dee2e6', padding: '10px', borderRadius: '8px' }}>
                <img src={product.image_url} alt={product.name} style={{ width: '100%', height: '150px', objectFit: 'cover' }} />
                <h3>{product.name}</h3>
                <p>${product.price}</p>
                <p>Stock: {product.stock}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'orders' && (
        <div>
          <h2>Orders</h2>
          {orders.map(order => (
            <div key={order.id} style={{ border: '1px solid #dee2e6', padding: '15px', marginBottom: '10px', borderRadius: '8px' }}>
              <h3>Order #{order.id}</h3>
              <p>User: {order.user_name}</p>
              <p>Total: ${order.total}</p>
              <p>Status: {order.status}</p>
              <select value={order.status} onChange={(e) => updateOrderStatus(order.id, e.target.value)}>
                <option value="pending">Pending</option>
                <option value="shipping">Shipping</option>
                <option value="completed">Completed</option>
                <option value="cancelled">Cancelled</option>
              </select>
            </div>
          ))}
        </div>
      )}

      {activeTab === 'users' && (
        <div>
          <h2>Users</h2>
          {users.map(user => (
            <div key={user.id} style={{ border: '1px solid #dee2e6', padding: '15px', marginBottom: '10px', borderRadius: '8px' }}>
              <h3>{user.name}</h3>
              <p>{user.email}</p>
              <p>Role: {user.role}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
