import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);

  const fetchCart = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      const response = await axios.get('http://localhost:8000/api/cart.php', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCartItems(response.data);
      calculateTotal(response.data);
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  }, []);

  useEffect(() => {
    fetchCart();
  }, [fetchCart]);

  const calculateTotal = (items) => {
    const sum = items.reduce((acc, item) => acc + (item.price * item.quantity), 0);
    setTotal(sum);
  };

  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    const token = localStorage.getItem('token');
    try {
      await axios.put('http://localhost:8000/api/cart.php', {
        product_id: productId,
        quantity: newQuantity
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (error) {
      console.error('Error updating cart:', error);
    }
  };

  const removeItem = async (productId) => {
    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:8000/api/cart.php?product_id=${productId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      fetchCart();
    } catch (error) {
      console.error('Error removing item:', error);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '20px auto', padding: '20px' }}>
      <h1>Shopping Cart</h1>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          {cartItems.map(item => (
            <div key={item.product_id} style={{ display: 'flex', alignItems: 'center', border: '1px solid #dee2e6', padding: '15px', marginBottom: '10px', borderRadius: '8px' }}>
              <img src={item.image_url} alt={item.name} style={{ width: '80px', height: '80px', objectFit: 'cover', marginRight: '15px' }} />
              <div style={{ flex: 1 }}>
                <h3>{item.name}</h3>
                <p>${item.price}</p>
              </div>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <button onClick={() => updateQuantity(item.product_id, item.quantity - 1)} style={{ padding: '5px 10px' }}>-</button>
                <span style={{ margin: '0 10px' }}>{item.quantity}</span>
                <button onClick={() => updateQuantity(item.product_id, item.quantity + 1)} style={{ padding: '5px 10px' }}>+</button>
              </div>
              <p style={{ margin: '0 15px', fontWeight: 'bold' }}>${(item.price * item.quantity).toFixed(2)}</p>
              <button onClick={() => removeItem(item.product_id)} style={{ padding: '5px 10px', backgroundColor: '#dc3545', color: 'white', border: 'none', borderRadius: '4px' }}>Remove</button>
            </div>
          ))}
          <div style={{ textAlign: 'right', marginTop: '20px' }}>
            <h2>Total: ${total.toFixed(2)}</h2>
            <Link to="/checkout" style={{ display: 'inline-block', padding: '10px 20px', backgroundColor: '#28a745', color: 'white', textDecoration: 'none', borderRadius: '4px' }}>
              Proceed to Checkout
            </Link>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
