import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchProduct();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/api/products.php?id=${id}`);
      setProduct(response.data);
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  const addToCart = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      setMessage('Please login to add to cart');
      return;
    }

    try {
      await axios.post('http://localhost:8000/api/cart.php', {
        product_id: id,
        quantity
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMessage('Added to cart successfully');
    } catch (error) {
      setMessage('Failed to add to cart');
    }
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: '800px', margin: '20px auto', padding: '20px' }}>
      <div style={{ display: 'flex', gap: '20px' }}>
        <img src={product.image_url} alt={product.name} style={{ width: '400px', height: '400px', objectFit: 'cover' }} />
        <div>
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#28a745' }}>${product.price}</p>
          <p>Stock: {product.stock}</p>
          <div style={{ margin: '20px 0' }}>
            <label>Quantity:</label>
            <input
              type="number"
              value={quantity}
              onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value)))}
              min="1"
              max={product.stock}
              style={{ marginLeft: '10px', padding: '5px' }}
            />
          </div>
          <button
            onClick={addToCart}
            style={{ padding: '10px 20px', backgroundColor: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
          >
            Add to Cart
          </button>
          {message && <p style={{ marginTop: '10px', color: message.includes('success') ? 'green' : 'red' }}>{message}</p>}
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
